package _05_Booleans;

public class _01_booleans {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean locked = false;
		
		if (locked ==  true){
			System.out.println("Homescreen is locked");
		}
		else {
			System.out.println("Homescreen is not locked");
		}
		
		
		boolean isTired = true;
		
		if (isTired == true){
			System.out.println("Go to bed!");
		}
		else {
			System.out.println("Get some work done!");
		}
		
		//Class example
		boolean tired = false;
		boolean awake = true;
		System.out.println(tired);
		System.out.println(2 > 1);
		System.out.println(2 < 1);
		
	}

}
